_cache_meta.query:
{
          assets(where: { slugs: ["polkadot"] }, limit: 1) {
            slug
            metrics(
              where: { metricKeys: ["staked_tokens"] }
              order: { createdAt: desc }
              limit: 1
            ) {
              metricKey
              defaultValue
              createdAt
            }
          }
        }
