_cache_meta.query:
{
              rewardOptions(
                where: {
                  inputAsset: { slugs: ["cosmos"] }
                  typeKeys: ["smart-contract"]
                }
                limit: 200
                order: { metricKey_desc: "staked_tokens" }
              ) {
                type {
                  key
                  label
                }

                inputAssets(limit: 1) {
                  slug
                  name
                  symbol
                }

                providers(limit: 1) {
                  slug
                  name
                  isActive
                }

                metrics(where: { metricKeys: ["staked_tokens", "reward_rate"] }, limit: 5) {
                  metricKey
                  defaultValue
                }
              }
            }
